import React, { useEffect, useState} from "react";
import { MathJax, MathJaxContext } from "better-react-mathjax";
import styles from './styles.module.css'

export const SearchbarComponent = (props) => {
  var [open, setOpen] = useState(false);
  var [ pageLoaded, setPageLoaded] = useState(true); 
  var [searchKeyword, setSearchKeyword] = useState(null);
  var [suggestions, setSuggestions] = useState([]);
  var [history, setHistory] = useState([]);
  var [inputFocus, setInputFocus] = useState(false);
  var [openMobileCam, setopenMobileCam] = useState(false);
  var [openDesktopPopup, setOpenDesktopPopup] = useState(false);
  var [imgUploaded, setimgUploaded] = useState(false);
  var [focusSearchbar, setFocusSearchbar] = useState(false);
  const typeDelay = 500;
  const origin = 'learnportal2'
  const API_ENDPOINT = 'test'
  const AUTH_KEY = 'Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJfaWQiOiI2MDVhMzc5MTg2M2YzYTViZTE0YjE0NzgiLCJyb2xlIjoiYnlqdS1hbnN3ZXItcmV2aWV3ZXIiLCJ0aW1lc3RhbXAiOiIyMDIxLTA1LTEwVDEwOjM0OjM4LjUyNloiLCJpYXQiOjE2MjA2NDI4Nzh9.f5xoVcgtvWAOwOnbc-VCMDjRwt7P4Pax5ftUL2TRUJQ';

  useEffect(()=>{
    console.log('Hello')
  },[]);


  useEffect(() => {
    const timeoutId = setTimeout(() => {
        if(searchKeyword){
            get_suggestions(searchKeyword, getCookie('docId'), getCookie('userToken')).then(r=>r.json()).then(res=>{
                var tempArray = [];

                for(var i=0;i<res['suggestions'].length;i++){
                    tempArray.push(
                        {
                            'question_text' : res['suggestions'][i]['mathjax_question'],
                            'question_id' : res['suggestions'][i]['qid'],
                            'href' : res['suggestions'][i]['href']
                        }
                    )
                }
                try{
                    setCookies('docId', res['docId'], {maxAge: 60 * 60 * 24, sameSite: "none", secure: true});
                }
                catch(err){}
                setSuggestions(tempArray);
            })
        }
        }, typeDelay);

    return () => clearTimeout(timeoutId);
    }, [searchKeyword]);

    function get_suggestions(text, doc_id, userId) {
        // return fetch("/question-answer/api/get_suggestions/", {
        //   method: "POST",
        //   body: JSON.stringify({ text: text, doc_id: doc_id, userid: userId }),
        // });

        const formData = new FormData();
        formData.append("text", text);
        formData.append("origin", origin)
        if(doc_id){
            formData.append("doc_id", parameters.doc_id);
        }

        return fetch(API_ENDPOINT + 'get_suggestions/', {
            method: 'POST',
            headers: {
                'Authorization' : AUTH_KEY,
                'X-Tnl-User-Id' : userId
            },
            body: formData,
        })

      }

  return(
          <div className={styles.byjus_search_widget_searchBarHolder}>
            <div className={styles.byjus_search_widget_searchBar}>
                <div className={styles.byjus_search_widget_searchbuttonHolder}>
                    <div className={styles.byjus_search_widget_buttonHolder}>
                        {/* <img className={styles.byjus_search_widget_icon} onClick={() => customSearch(searchKeyword)} src={assetURL + "/Search.png"} width="44" height="44" alt="SearchIcon"></img> */}
                        <img  onClick={() => customSearch(searchKeyword)} src={"https://search-static-stg.byjusweb.com/assets/SearchIcon1.png"} width="24" height="24" alt="SearchIcon"></img>
                    </div>
                </div>
                <div>
                    <input id="searchInput" name="searchInput" type="text" placeholder={`Search for questions & chapters`}  onFocus={() => {setInputFocus(true);showHistory(5);}} onKeyPress={(e) => {enterButtonPressed(e)}} onKeyUp={(e)=>searchInput(e.target.value)} autoComplete="off"></input>
                </div>
                <div className={styles.byjus_search_widget_buttonContainer}>
                    <div className={styles.byjus_search_widget_buttonHolder}>
                        { (inputFocus || suggestions.length>0 || history.length>0) && <div className={styles.byjus_search_widget_crossButtonHolder}>
                            <img className={styles.byjus_search_widget_icon} onClick={() => clearSearch()} src={"https://search-static-stg.byjusweb.com/assets/cancel.webp"} width="14" height="14" alt="CrossIcon"></img>
                        </div>
                        }
                    </div>

                    <div className={ inputFocus == true ? (styles.byjus_search_widget_buttonHolder + ' ' + styles.showindesktop) : (styles.byjus_search_widget_buttonHolder)}>
                        <div className={styles.byjus_search_widget_showinmobile}>
                            <div className={styles.byjus_search_widget_cameraButtonHolder1}>
                                <img className={styles.byjus_search_widget_icon} onClick={() => cameraClick()} src={"https://search-static-stg.byjusweb.com/assets/Camera.png"} width="24" height="24" alt="CameraIcon"></img>
                            </div>
                        </div>
                        <div className={styles.byjus_search_widget_showindesktop}>
                            <div className={styles.byjus_search_widget_cameraButtonHolder2}>
                                <img className={styles.byjus_search_widget_icon} onClick={() => imgSearch()} src={"https://search-static-stg.byjusweb.com/assets/Camera.png"} width="24" height="24" alt="CameraIcon"></img>
                            </div>
                        </div>
                    </div>
                </div>

                {suggestions.length>0 &&
                    <div className={styles.byjus_search_widget_suggestionHolder}>
                        {suggestions.map((el,i) =>
                        <div key={'suggestion_' + i}>
                        {el.href &&
                        <a  href={el.href ? (absolutePath + '/question-answer/' + el.href + '/') : (absolutePath + '/question-answer/search/ques?id=' + el.question_id)} style={{textDecoration: 'none', color: 'unset'}}>
                            <div className={styles.byjus_search_widget_suggestionBox} onClick={() => {openQuestion(el, i);}}>
                                <div>
                                <MathJaxContext config={MathjaxConfig}>
                                    <MathJax>
                                        <div dangerouslySetInnerHTML={{ __html: removeOptions(el.question_text) || ''}}></div>
                                    </MathJax>
                                </MathJaxContext>
                                </div>
                                <div>
                                    <img src={"https://search-static-stg.byjusweb.com/assets/SearchRecommendation.png"} width="24" height="24" alt="search-recommendation"></img>
                                </div>
                            </div>
                        </a>
                        }
                        </div>
                        )}
                    </div>
                }
                {suggestions.length == 0 && history.length > 0 && !searchKeyword &&
                    <div className={styles.byjus_search_widget_suggestionHolder}>
                        {history.map((el,i) =>
                          <a key={'history_' + i} href={el.href ? (absolutePath + '/question-answer/' + el.href + '/') : (absolutePath + '/question-answer/search/' + encodeURI(encode(el.searchText)))} style={{textDecoration: 'none', color: 'unset'}}>
                            <div className={styles.byjus_search_widget_historyBox} onClick={() => {openHistory(el);}}>
                                <div className={styles.byjus_search_widget_clockIcon}>
                                    <img src={"https://search-static-stg.byjusweb.com/assets/SearchHistory.png"} width="20" height="20" alt="search-history"></img>
                                </div>
                                <div>
                                    <MathJaxContext config={MathjaxConfig}>
                                        <MathJax>
                                            <div dangerouslySetInnerHTML={{ __html: removeOptions(el.searchText) || ''}}></div>
                                        </MathJax>
                                    </MathJaxContext>
                                </div>
                                <div>
                                    <img src={"https://search-static-stg.byjusweb.com/assets/SearchRecommendation.png"} width="24" height="24" alt="search-recommendation"></img>
                                </div>
                            </div>
                        </a>
                        )}
                    </div>
                }
            </div>
        </div>
  )
}
